import { UserFilterData } from 'components/UserFilter';
import React, { useState } from 'react';
import Modal from 'react-modal';


type Props = {
  isOpen: boolean;
  onRequestClose: () => void;
  onSubmit: (filterData: UserFilterData) => void;
};

const UserSearchModal = ({ isOpen, onRequestClose, onSubmit }: Props) => {
  const [filterData, setFilterData] = useState<UserFilterData>({});

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFilterData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    onSubmit(filterData);
    onRequestClose();
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      overlayClassName="modal-overlay"
      className="modal-content"
    >
      <h2>Buscar usu√°rios</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="filterField">Filtrar por:</label>
          <select
            id="filterField"
            name="filterField"
            className="form-control"
            value={filterData.filterField || ''}
            onChange={handleInputChange}
          >
            <option value="">Escolha um campo</option>
            <option value="firstName">Nome</option>
            <option value="lastName">Sobrenome</option>
            <option value="email">Email</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="filterValue">Buscar:</label>
          <input
            type="text"
            id="filterValue"
            name="filterValue"
            className="form-control"
            value={filterData.filterValue || ''}
            onChange={handleInputChange}
          />
        </div>
        <div className="form-group text-right">
          <button type="submit" className="btn btn-primary mr-2">
            Buscar
          </button>
          <button type="button" className="btn btn-secondary" onClick={onRequestClose}>
            Cancelar
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default UserSearchModal;
